import profileAvatar from '../images/profile-avatar.jpg';
import { PopupProfileEdit } from './PopupProfileEdit';
import { PopupCardsAdd } from './PopupCardsAdd';
import { PopupEditAvatar } from './PopupEditAvatar';
import { ImagePopup } from './ImagePopup';
import { PopupRemoveCard } from './PopupRemoveCard';
import React, { useEffect, useState } from 'react';
import { api } from './utils/Api';

function Main(props) {
  const [userName, setUserName] = useState('');
  const [userDescription, setUserDescription] = useState('');
  const [userAvatar, setUserAvatar] = useState(profileAvatar);
  const [cards, setCards] = useState([]);

  useEffect(() => {
    Promise.resolve(api.getUserInfo()).then((userInfo) => {
      console.log(userInfo);

      setUserName(userInfo.name);
      setUserDescription(userInfo.about);
      setUserAvatar(userInfo.avatar);
    });

    Promise.resolve(api.getCards()).then((usersCards) => {
      setCards(usersCards);
      // console.log(cards);
    });
  });

  // useEffect(() => {
  //   Promise.resolve(api.getCards()).then((cards) => {
  //     console.log(cards);
  //   });
  // });

  return (
    <>
      <main className="content">
        <section className="profile content__profile">
          <div className="profile__info-container">
            <button className="profile__avatar-button" onClick={props.onEditAvatar}>
              <img className="profile__avatar" src={userAvatar} alt="Аватар профиля" />
            </button>
            <p className="profile__description">{userDescription}</p>
            <div className="profile__name-button-container">
              <h1 className="profile__name">{userName}</h1>
              <button
                className="profile__edit-button"
                type="button"
                aria-label="Редактировать описание профиля"
                onClick={props.onEditProfile}
              />
            </div>
          </div>
          <button
            className="profile__add-button"
            type="button"
            aria-label="Добавить фотографию"
            onClick={props.onAddPlace}
          />
        </section>
        <section className="elements content__elements" />
        <PopupProfileEdit isOpened={props.isEditProfileOpen} onClose={props.onClose} />
        <PopupCardsAdd isOpened={props.isAddPlacePopupOpen} onClose={props.onClose} />
        <PopupEditAvatar isOpened={props.isEditAvatarPopupOpen} onClose={props.onClose} />
        <PopupRemoveCard isOpened={''} /> {/*-------------------*/}
        <ImagePopup />
        <template id="cards-template">
          <div class="elements__card">
            <img class="elements__image" src="#" alt="Фотография" />
            <div class="elements__wrapper">
              <h2 class="elements__place-name"></h2>
              <div class="elements__like-container">
                <button class="elements__like" type="button" aria-label="Понравилось"></button>
                <span class="elements__like-counter"></span>
              </div>
            </div>
            <button class="elements__bucket elements__bucket_type_hidden" type="button" aria-label="Удалить"></button>
          </div>
        </template>
      </main>
    </>
  );
}

export { Main };
