import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import * as auth from '../auth.js';

function Login() {
  function handleChange(e) {
    const { name, value } = e.target;

    setFormValue({
      ...formValue,
      [name]: value,
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    // здесь нужно будет добавить логин
    if (!formValue.username || !formValue.password) {
      return;
    }
    auth
      .authorize(formValue.username, formValue.password)
      .then((data) => {
        // нужно проверить, есть ли у данных JWT
        // сбросьте стейт, затем в колбэке установите
        // стейт loggedIn родительского App как true,
        // затем перенаправьте его в /diary
      })
      .catch((err) => console.log(err));
  }

  return (
    <form className="enterance-form" name={'sign-in'} onSubmit={handleSubmit}>
      <h1 className="enterance-form__title">Вход</h1>
      <input className="enterance-form__input" placeholder="Email"></input>
      <input className="enterance-form__input" placeholder="Пароль"></input>
      <button className="enterance-form__submit-button" type="submit">
        Войти
      </button>
    </form>
  );
}

export { Login };
