// import './App.css';
import { Header } from './Header';
import { Main } from './Main';
import { Footer } from './Footer';

function App() {
  function handleAvatarClick() {
    document.querySelector('.popup_type_edit-avatar').classList.add('popup_opened');
  }

  function handleEditProfileCLick() {
    document.querySelector('.popup_type_profile-edit').classList.add('popup_opened');
  }

  function handleAddCardClick() {
    document.querySelector('.popup_type_cards-add').classList.add('popup_opened');
  }

  const [isEditProfileOpen, setIsEditProfileOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

  return (
    <>
      <body className="page">
        <Header />
        <Main onEditAvatar={handleAvatarClick} onEditProfile={handleEditProfileCLick} onAddPlace={handleAddCardClick} />
        <Footer />
      </body>
    </>
  );
}

export default App;
