import { Route, Routes, NavLink } from 'react-router-dom';

function NavBar() {
  const currentPath = window.location.pathname;

  function getButton(currentPath) {
    switch (currentPath) {
      case '/sign-in':
        return <button className="header__navigation-button">Регистрация</button>;
      case '/sign-up':
        return <button className="header__navigation-button">Войти</button>;
      case '/':
        return <button className="header__navigation-button">Выйти</button>;
    }
  }

  return (
    <>
      <Routes>
        <Route path="*" element={getButton(currentPath)} />
        {/* <Route path="/sign-in" element={getButton(currentPath)} />
        <Route path="/sign-up" element={getButton(currentPath)} /> */}

        <Route
          path="/"
          element={
            <div className="header__navigation_to-logout">
              <span className="header__navigation-item_email">{userMail}</span>
              <NavLink className="header__navigation-item_link" to="/sign-in">
                Выйти
              </NavLink>
            </div>
          }
        />
      </Routes>
    </>
  );
}

export { NavBar };
