import { PopupWithForm } from './PopupWithForm';
import { useRef } from 'react';

function PopupCardsAdd({ isOpened, onClose, onAddPlace }) {
  function handleSubmit(e) {
    e.preventDefault();

    onAddPlace({
      name: values.name,
      link: values.link,
    });
  }

  return (
    <PopupWithForm
      popupType="cards-add"
      name="Новое место"
      isOpened={isOpened}
      onClose={onClose}
      buttonText={'Создать'}
      onSubmit={handleSubmit}
    >
      <input
        className="popup__input popup__input_type_place-name"
        id="place-name"
        placeholder="Название"
        name="cards-input-place-name"
        defaultValue=""
        required=""
        minLength={2}
        maxLength={30}
        onChange={''}
      />
      <span className="popup__error popup__error_type_add-card" id="place-name-error" />
      <input
        className="popup__input popup__input_type_img-link"
        id="img-link"
        placeholder="Ссылка на картинку"
        name="cards-input-img-link"
        defaultValue=""
        required=""
        type="url"
      />
      <span className="popup__error popup__error_type_img-link" id="img-link-error" />
    </PopupWithForm>
  );
}

export { PopupCardsAdd };
